import { Request, Response } from 'express';
import responseCodes from '../general/responseCodes';
import { INewLector } from './interfaces';
import lecturerService from './service';

const lecturerController = {
  getAllLecturersById: async (req: Request, res: Response) => {
    const lecturers = await lecturerService.getAllLecturers();
    // console.log(lecturers);
    if (lecturers) {
      return res.status(responseCodes.ok).json({ lecturers });
    }
    return res.status(responseCodes.ServerError).json({
      error: 'Server error',
    });
  },
  getLecturersSubjects: async (req: Request, res: Response) => {
    const lecturersActiveSubjects = await lecturerService.getLecturersSubjects();
    if (lecturersActiveSubjects) {
      return res.status(responseCodes.ok).json({
        lecturersActiveSubjects,
      });
    }
    return res.status(responseCodes.ServerError).json({
      error: 'Server error',
    });
  },
  getLecturerById: async (req: Request, res: Response) => {
    const id: number = parseInt(req.params.id, 10);
    const lecturer = await lecturerService.getLecturerById(id);
    if (!id) {
      return res.status(responseCodes.badRequest).json({
        error: 'No valid id provided',
      });
    }
    if (lecturer === undefined) {
      return res.status(responseCodes.badRequest).json({
        error: `No lecturer found with id: ${id}`,
      });
    }
    if (!lecturer) {
      return res.status(responseCodes.ServerError).json({
        error: 'Server error',
      });
    }
    return res.status(responseCodes.ok).json({
      lecturer,
    });
  },

  // ÃppejÃµu kustutamine ainult siis kui tal antavaid ained pole.

  deleteLecturerWhenNoSubjectsById: async (req: Request, res: Response) => {
    const id: number = parseInt(req.params.id, 10);
    if (!id) {
      return res.status(responseCodes.badRequest).json({
        error: 'No valid id provided',
      });
    }
    const subjectExists = await lecturerService.deleteLecturerById(id);
    if (subjectExists === undefined) {
      return res.status(responseCodes.badRequest).json({
        message: `Lecturer not found with id: ${id}`,
      });
    }
    if (subjectExists) {
      return res.status(responseCodes.noContent).send();
    }
    return res.status(responseCodes.ServerError).json({
      error: 'Server error',
    });
  },

  // Uue ÃppejÃµu lisamine

  addLecturer: async (req: Request, res: Response) => {
    const { firstName, lastName, email } = req.body;
    // console.log(firstName, lastName, email);
    if (!firstName) {
      return res.status(responseCodes.badRequest).json({
        error: 'First name is required',
      });
    }
    if (!lastName) {
      return res.status(responseCodes.badRequest).json({
        error: 'Last name is required',
      });
    }
    if (!email) {
      return res.status(responseCodes.badRequest).json({
        error: 'Email is required',
      });
    }
    const newLecturer: INewLector = {
      firstName,
      lastName,
      email,
    };
    const id = await lecturerService.createlecturer(newLecturer);
    if (id) {
      return res.status(responseCodes.created).json({
        id,
      });
    }
    return res.status(responseCodes.ServerError).json({
      error: 'Server error',
    });
  },
  updateLecturerById: async (req: Request, res: Response) => {
    const id: number = parseInt(req.params.id, 10);
    const { firstName, lastName, email } = req.body;
    if (!id) {
      return res.status(responseCodes.badRequest).json({
        error: 'No valid id provided',
      });
    }
    if (!firstName) {
      return res.status(responseCodes.badRequest).json({
        error: 'Provide firstname',
      });
    }
    if (!lastName) {
      return res.status(responseCodes.badRequest).json({
        error: 'Provide lastname',
      });
    }
    if (!email) {
      return res.status(responseCodes.badRequest).json({
        error: 'Email is required',
      });
    }
    const newLecturer: INewLector = {
      firstName,
      lastName,
      email,
    };
    const lecturerExists = await lecturerService.updateLecturerById(
      newLecturer,
      id,
    );
    if (lecturerExists === undefined) {
      return res.status(responseCodes.badRequest).json({
        error: `No user found with id: ${id}`,
      });
    }
    if (lecturerExists) {
      return res.status(responseCodes.noContent).send();
    }
    return res.status(responseCodes.ServerError).json({
      error: 'Server error',
    });
  },
};

export default lecturerController;

/* eslint-disable import/extensions */
/* eslint-disable import/no-unresolved */
import { FieldPacket, ResultSetHeader } from 'mysql2';
import pool from '../../database';
import { ILector, INewLector, ILecturerSubjects } from './interfaces';

const lecturerService = {
  getAllLecturers: async (): Promise<ILector[] | false> => {
    try {
      const [lecturers]: [ILector[], FieldPacket[]] = await pool.query(
        'SELECT * FROM lecturers WHERE dateDeleted IS NULL',
      );
      return lecturers;
    } catch (error) {
      // console.log(error);
      return false;
    }
  },
  getLecturersSubjects: async (): Promise<ILecturerSubjects[] | false> => {
    try {
      const [activeSubjects]: [ILecturerSubjects[], FieldPacket[]] = await pool.query(
        `SELECT CONCAT(lecturers.firstName," ",lecturers.lastName) AS fullName,
          (SELECT GROUP_CONCAT(subject) FROM subjects 
          WHERE lecturers.id = lecturers_id) AS activeSubjects 
          FROM lecturers INNER JOIN subjects ON lecturers.id = subjects.lecturers_id 
          WHERE lecturers.dateDeleted IS NULL 
          GROUP BY fullName, activeSubjects ORDER BY activeSubjects DESC;`,
      );
      return activeSubjects;
    } catch (error) {
      return false;
    }
  },
  getLecturerById: async (
    id: number,
  ): Promise<ILector[] | false | undefined> => {
    try {
      const lecturer: [ILector[], FieldPacket[]] = await pool.query(
        'SELECT firstName, lastName FROM lecturers WHERE id = ? AND dateDeleted IS NULL',
        [id],
      );
      if (lecturer[0][0] !== undefined) {
        return lecturer[0];
      }
      return false;
    } catch (error) {
      return false;
    }
  },
  deleteLecturerById: async (
    id: number,
  ): Promise<number | false | undefined> => {
    try {
      const [index]: [ResultSetHeader, FieldPacket[]] = await pool.query(
        'UPDATE lecturers SET dateDeleted = ? WHERE dateDeleted IS NULL AND id = ?',
        [new Date(), id],
      );
      if (index.affectedRows > 0) {
        return index.affectedRows;
      }
      return false;
    } catch (error) {
      return false;
    }
  },
  createlecturer: async (newLecturer: INewLector): Promise<number | false> => {
    try {
      const [result]: [ResultSetHeader, FieldPacket[]] = await pool.query(
        'INSERT INTO lecturers SET ? ',
        [{ ...newLecturer }],
      );
      return result.insertId;
    } catch (error) {
      return false;
    }
  },
  updateLecturerById: async (
    newLecturer: INewLector,
    id: number,
  ): Promise<boolean | undefined> => {
    try {
      const [update]: [ResultSetHeader, FieldPacket[]] = await pool.query(
        'UPDATE lecturers SET ? WHERE id = ?',
        [{ ...newLecturer }, id],
      );
      if (update.affectedRows > 0) {
        return true;
      }
      return false;
    } catch (error) {
      return false;
    }
  },
};

export default lecturerService;

/* eslint-disable import/extensions */
/* eslint-disable import/no-unresolved */
import { Request, Response } from 'express';
import responseCodes from '../general/responseCodes';
import { INewSubject } from './interfaces';

import subjectServices from './service';

const subjectController = {
  getAllSubjects: async (req: Request, res: Response) => {
    const subjects = await subjectServices.getAllSubjects();
    if (subjects) {
      return res.status(responseCodes.ok).json({ subjects });
    }
    return res.status(responseCodes.ServerError).json({
      error: 'Server error',
    });
  },
  getSubjectById: async (req: Request, res: Response) => {
    const id: number = parseInt(req.params.id, 10);
    const { subjectCode } = req.body;
    if (!subjectCode && !id) {
      return res.status(responseCodes.badRequest).json({
        error: 'No valid id or subjedtCode provided',
      });
    }

    if (!id) {
      const subject = await subjectServices.getSubjectByCode(subjectCode);
      if (subject === false) {
        return res.status(responseCodes.ServerError).json({
          error: 'Server error',
        });
      }
      if (!subject) {
