      return res.status(responseCodes.badRequest).json({
        error: 'No valid id or subjectCode provided',
      });
    }

    const homework = await homeworkService.gethomeworkId(id);

    if (homework === undefined) {
      return res.status(responseCodes.badRequest).json({
